import React, { useCallback } from "react";
import AppLayout from "../components/AppLayout";
import { Form, Input, Button } from "antd";
import { useDispatch } from "react-redux";
import { SIGN_IN_REQUEST } from "../reducers/user";
import { useSelector } from "react-redux";
import styled from "styled-components";

const LoginForm = styled(Form)`
  padding: 20px;
  weith: 80%;
  margin-top: 30px;
`;

const Login = () => {
  const { st_signInLoading } = useSelector((state) => state.user);
  const dispatch = useDispatch();
  const onSubmit = useCallback((values) => {
    dispatch({
      type: SIGN_IN_REQUEST,
      data: values,
    });
  });

  return (
    <AppLayout>
      <LoginForm
        name="loginForm"
        labelCol={{ xs: 24, sm: 8 }}
        wrapperCol={{ xs: 24, sm: 16 }}
        onFinish={onSubmit}
      >
        <Form.Item
          label="email"
          name="email"
          rules={[{ required: true, message: "Please Input Your Email" }]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          label="password"
          name="password"
          rules={[{ required: true, message: "Your Must Wreite Password" }]}
        ></Form.Item>

        <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
          <Button type="primary" htmlType="submit" loading={st_signInLoading}>
            Login
          </Button>
        </Form.Item>
      </LoginForm>
    </AppLayout>
  );
};

export default Login;
