import React, { useCallback, useRef } from "react";
import { Form, Input, Button } from "antd";
import styled from "styled-components";

const FeedCreatBox = styled.div`
  width: 550px;
  padding: 20px 0px;
  margin-bottom: 10px;
`;

const FeedContent = styled(Input.TextArea)`
  resize: none;
  width: 100%;
  height: 80px;
`;

const FeedCreatForm = () => {
  const imageFile = useRef();

  const fileButtonclick = useCallback(() => {
    imageFile.current.click();
  });

  const fileChange = useCallback(
    (e) => {
      console.log(e.target.files);
    },
    [imageFile.current]
  );

  return (
    <FeedCreatBox>
      <FeedContent allowClear={true} placeholder="New Content..." />
      <Input type="file" hidden ref={imageFile} accept=".png, .jpg" />

      <Button type="primary" onClick={fileButtonclick}>
        Upload
      </Button>
    </FeedCreatBox>
  );
};

export default FeedCreatForm;
