import React, { useRef } from "react";
import { Form, Input, Button } from "antd";
import styled from "styled-components";

const FeedCreatBox = styled.div`
  width: 550px;
  padding: 20px 0px;
  margin-bottom: 10px;
`;

const FeedContent = styled(Input.TextArea)`
  resize: none;
  width: 100%;
  height: 80px;
`;

const FeedCreatForm = () => {
  const imageFile = useRef();

  return (
    <FeedCreatBox>
      <FeedContent allowClear={true} placeholder="New Content..." />
      <Input type="file" hidden ref={imageFile} />
    </FeedCreatBox>
  );
};

export default FeedCreatForm;
