import React from "react";
import styled from "styled-components";
import { WholeWrapper, Wrapper } from "../../components/commonComponents";
import Theme from "../../components/Theme";

const Title = styled.h1`
  font-size: 4.5rem;
  color: ${Theme.white_C};
  line-height: 0.7;
`;

const Desc = styled.p`
  color: ${Theme.white_C};
  font-style: italic;
  font-size: 1.1rem;
  line-height: 1.3;
`;

const CommonTitle = styled.h2`
  font-size: 3rem;
  position: relative;
  margin-top: 80px;

  &:before {
    content: "";
    width: 5px;
    height: 130px;
    background: ${Theme.basicTheme_C};
    position: absolute;
    top: -130px;
    left: 50%;
    margin-left: -2.5px;
  }
`;

const Css = () => {
  return (
    <WholeWrapper>
      <Wrapper
        height={`100vh`}
        bgImg={`url("https://4leaf-bird-s3.s3.ap-northeast-2.amazonaws.com/thumb/image1.png")`}
      >
        <Title>Welcome to Mountain</Title>
        <Title>Expeditions Club!</Title>

        <Desc color={Theme.white_C}>
          Overseas Expeditions, UK Mountain Challenges
        </Desc>
        <Desc color={Theme.white_C}>Private Guiding &#38; Instruction</Desc>
      </Wrapper>

      <Wrapper>
        <CommonTitle>About Us</CommonTitle>
      </Wrapper>
    </WholeWrapper>
  );
};

export default Css;
