import React, { useState } from "react";
import { Row, Col } from "antd";
import styled from "styled-components";

const MenuRow = styled(Col)`
  height: 30px;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: flex-start;
  border-bottom: 0.5px solid #ffccff;

  box-sizing: border-box;
`;

const IconCol = styled(Col)`
  height: 30px;
  margin-right: 5px;
  padding: 5px 10px;
`;

const ContentCol = styled(Col)`
  height: 30px;
  padding: 5px 10px;
`;

const MyMenu = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(true);

  return isLoggedIn ? (
    <MenuRow>
      <>
        <IconCol span={4}> ICON </IconCol>
        <ContentCol span={20}> CONTENT </ContentCol>
      </>
    </MenuRow>
  ) : (
    <div>login 필요</div>
  );
};

export default MyMenu;
